
public class SwapTwoNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=22, b=55, temp;
		System.out.println("Before swapping:"+a+"  "+b);
		temp =a;
		a=b;
		b=temp;/* a=a+b;
		b=a-b;
		a=a-b;
		*/
		System.out.print("After swapping:" +a+"  "+b);

	}

}
